import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import AccountDetails from './AccountDetails';
import ProfileEditor from './ProfileEditor';
import ColorPicker from './ColorPicker';

// Helper function to get the emoji for an icon name
const getIconEmoji = (iconName) => {
  const icons = {
    'default': 'ðŸ‘¤',
    'smile': 'ðŸ˜Š',
    'heart': 'â¤ï¸',
    'star': 'â­',
    'moon': 'ðŸŒ™',
    'sun': 'â˜€ï¸',
    'flower': 'ðŸŒ¸',
    'tree': 'ðŸŒ³',
    'cat': 'ðŸ±',
    'dog': 'ðŸ¶',
    'coffee': 'â˜•',
    'book': 'ðŸ“š',
    'music': 'ðŸŽµ',
    'camera': 'ðŸ“·',
    'rocket': 'ðŸš€',
    'rainbow': 'ðŸŒˆ',
  };

  return icons[iconName] || icons.default;
};

const Header = () => {
  const { user } = useAuth();
  const [showAccountDetails, setShowAccountDetails] = useState(false);
  const [showProfileEditor, setShowProfileEditor] = useState(false);

  // Record when the user first created their account
  useEffect(() => {
    if (user && user.username) {
      const userSinceKey = `userSince_${user.username}`;
      if (!localStorage.getItem(userSinceKey)) {
        localStorage.setItem(userSinceKey, new Date().toISOString());
      }
    }
  }, [user]);

  return (
    <header className="header">
      <Link to="/">
        <h1 className="app-title">Mood Journal</h1>
      </Link>
      <div className="header-controls">
        {user && (
          <div className="user-controls">
            <div 
              className="user-profile-icon"
              onClick={() => setShowProfileEditor(true)}
              title="Edit Profile"
            >
              {getIconEmoji(user.icon || 'default')}
            </div>
            <div className="user-name-container">
              <span className="username" style={{ color: user.nameColor || '#3498db' }}>
                Hello, {user.displayName || user.username}
              </span>
              <ColorPicker />
            </div>
            <button 
              onClick={() => setShowAccountDetails(true)} 
              className="account-button"
            >
              My Account
            </button>
          </div>
        )}
      </div>

      {showAccountDetails && (
        <AccountDetails onClose={() => setShowAccountDetails(false)} />
      )}

      {showProfileEditor && (
        <ProfileEditor onClose={() => setShowProfileEditor(false)} />
      )}
    </header>
  );
};

export default Header;
