import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useJournal } from '../context/JournalContext';
import { useAuth } from '../context/AuthContext';
import JournalItem from './JournalItem';
import Dashboard from './Dashboard';
import Welcome from './Welcome';
import TagFilter from './TagFilter';

const JournalList = () => {
  const { entries, createEntry, getEntriesByTag } = useJournal();
  const { user } = useAuth();
  const [showDashboard, setShowDashboard] = useState(false);
  const [showWelcomeMessage, setShowWelcomeMessage] = useState(false);
  const [selectedTag, setSelectedTag] = useState(null);
  const [filteredEntries, setFilteredEntries] = useState([]);
  const [showTagFilter, setShowTagFilter] = useState(false);
  const navigate = useNavigate();

  // Check if this is the user's first visit after login
  useEffect(() => {
    const hasSeenWelcome = localStorage.getItem(`welcomeSeen_${user.username}`);

    if (!hasSeenWelcome && user) {
      setShowWelcomeMessage(true);
      localStorage.setItem(`welcomeSeen_${user.username}`, 'true');
    }
  }, [user]);

  // Filter entries by tag
  useEffect(() => {
    if (selectedTag) {
      setFilteredEntries(getEntriesByTag(selectedTag));
    } else {
      setFilteredEntries(entries);
    }
  }, [selectedTag, entries, getEntriesByTag]);

  const handleNewEntry = () => {
    // Set flag to indicate this is an intentional creation
    sessionStorage.setItem('intentionalCreation', 'true');
    const newEntry = createEntry();
    navigate(`/edit/${newEntry.id}`);
  };

  const toggleDashboard = () => {
    setShowDashboard(prev => !prev);
    // Hide tag filter when dashboard is shown
    if (!showDashboard) {
      setShowTagFilter(false);
    }
  };

  const toggleTagFilter = () => {
    setShowTagFilter(prev => !prev);
    // Hide dashboard when tag filter is shown
    if (!showTagFilter) {
      setShowDashboard(false);
    }
  };

  const handleTagSelect = (tag) => {
    setSelectedTag(tag);
  };

  const displayedEntries = selectedTag ? filteredEntries : entries;

  return (
    <div>
      {showWelcomeMessage && <Welcome />}

      <div className="journal-controls">
        <button onClick={handleNewEntry} className="new-entry-button">
          Create New Journal Entry
        </button>

        <div className="journal-view-controls">
          {entries.length > 0 && (
            <>
              <button 
                onClick={toggleTagFilter} 
                className={`filter-toggle ${showTagFilter ? 'active' : ''}`}
                title="Filter by tags"
              >
                <span className="filter-icon">üè∑Ô∏è</span>
                <span>Filter</span>
              </button>

              <button 
                onClick={toggleDashboard} 
                className={`dashboard-toggle ${showDashboard ? 'active' : ''}`}
              >
                {showDashboard ? 'Hide Insights' : 'View Mood Insights'}
              </button>
            </>
          )}
        </div>
      </div>

      {selectedTag && (
        <div className="active-filter-banner">
          <div className="filter-info">
            <span>Filtering by tag:</span>
            <span className="active-tag-pill">{selectedTag}</span>
          </div>
          <button 
            className="clear-filter-button"
            onClick={() => setSelectedTag(null)}
          >
            Clear Filter
          </button>
        </div>
      )}

      <div className="journal-content">
        {showTagFilter && entries.length > 0 && (
          <div className="tag-filter-sidebar">
            <TagFilter 
              selectedTag={selectedTag}
              onSelectTag={handleTagSelect}
            />
          </div>
        )}

        <div className={`journal-main ${showTagFilter ? 'with-sidebar' : ''}`}>
          {showDashboard && entries.length > 0 && (
            <Dashboard onClose={toggleDashboard} />
          )}

          {displayedEntries.length === 0 ? (
            selectedTag ? (
              <div className="empty-state">
                <h2>No entries with tag "{selectedTag}"</h2>
                <p>Try selecting a different tag or clear the filter.</p>
                <button 
                  onClick={() => setSelectedTag(null)} 
                  className="clear-filter-button large"
                >
                  Clear Filter
                </button>
              </div>
            ) : entries.length === 0 ? (
              <div className="empty-state">
                <h2>No journal entries yet</h2>
                <p>Start tracking your mood by creating your first journal entry!</p>
                <button onClick={handleNewEntry} className="new-entry-button">
                  Create Your First Entry
                </button>
              </div>
            ) : null
          ) : (
            <div className="journal-list">
              {displayedEntries.map(entry => (
                <Link to={`/edit/${entry.id}`} key={entry.id}>
                  <JournalItem entry={entry} />
                </Link>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default JournalList;
