import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

const Welcome = () => {
  const { user } = useAuth();
  const [showWelcome, setShowWelcome] = useState(true);
  const navigate = useNavigate();
  
  useEffect(() => {
    // Auto-hide welcome message after 5 seconds
    const timer = setTimeout(() => {
      setShowWelcome(false);
    }, 5000);
    
    return () => clearTimeout(timer);
  }, []);
  
  const handleStartJournal = () => {
    setShowWelcome(false);
    navigate('/new');
  };
  
  if (!showWelcome) return null;
  
  return (
    <div className="welcome-container">
      <div className="welcome-card">
        <div className="welcome-header">
          <h2>Welcome to Mood Journal, {user.username}!</h2>
          <button 
            className="welcome-close" 
            onClick={() => setShowWelcome(false)}
          >
            √ó
          </button>
        </div>
        <div className="welcome-content">
          <p>Track your moods, reflect on your feelings, and gain insights into your emotional well-being.</p>
          <div className="welcome-features">
            <div className="welcome-feature">
              <div className="feature-icon">üìù</div>
              <div className="feature-text">Record daily entries</div>
            </div>
            <div className="welcome-feature">
              <div className="feature-icon">üòä</div>
              <div className="feature-text">Track your moods</div>
            </div>
            <div className="welcome-feature">
              <div className="feature-icon">üìä</div>
              <div className="feature-text">View mood insights</div>
            </div>
            <div className="welcome-feature">
              <div className="feature-icon">üôè</div>
              <div className="feature-text">Practice gratitude</div>
            </div>
          </div>
        </div>
        <div className="welcome-footer">
          <button 
            className="start-journal-button"
            onClick={handleStartJournal}
          >
            Start Your First Entry
          </button>
        </div>
      </div>
    </div>
  );
};

export default Welcome;
