import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import IconSelector from './IconSelector';

const ProfileEditor = ({ onClose }) => {
  const { user, updateUser, logout } = useAuth();
  const navigate = useNavigate();
  
  // State for profile data
  const [profile, setProfile] = useState({
    username: user?.username || '',
    displayName: user?.displayName || user?.username || '',
    icon: 'default',
    nameColor: user?.nameColor || '#3498db',
    bio: '',
    currentPassword: '',
    newPassword: '',
    confirmPassword: '',
  });
  
  // State for theme data
  const [theme, setTheme] = useState({
    primaryColor: '#3498db', // Default blue
    secondaryColor: '#2ecc71', // Default green
    accentColor: '#f1c40f', // Default yellow (for gratitude, etc.)
    dangerColor: '#e74c3c', // Default red
    backgroundColor: '#f5f5f5', // Default light background
    cardColor: '#ffffff', // Default card background
    darkMode: false, // Default to light mode
  });
  
  const [activeTab, setActiveTab] = useState('general');
  const [showDeleteConfirm, setShowDeleteConfirm] = useState(false);
  const [message, setMessage] = useState({ type: '', text: '' });
  
  // Load existing profile and theme data if available
  useEffect(() => {
    const loadProfileData = () => {
      if (!user || !user.username) return;
      
      const profileKey = `userProfile_${user.username}`;
      const savedProfile = localStorage.getItem(profileKey);
      
      if (savedProfile) {
        const parsedProfile = JSON.parse(savedProfile);
        setProfile(prev => ({
          ...prev,
          displayName: parsedProfile.displayName || user.username,
          icon: parsedProfile.icon || 'default',
          nameColor: parsedProfile.nameColor || '#3498db',
          bio: parsedProfile.bio || '',
        }));
      }
      
      // Load theme data
      const themeKey = `userTheme_${user.username}`;
      const savedTheme = localStorage.getItem(themeKey);
      
      if (savedTheme) {
        setTheme(JSON.parse(savedTheme));
      }
    };
    
    loadProfileData();
  }, [user]);
  
  // Handle input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setProfile(prev => ({
      ...prev,
      [name]: value
    }));
  };
  
  // Handle theme changes
  const handleThemeChange = (e) => {
    const { name, value } = e.target;
    setTheme(prev => ({
      ...prev,
      [name]: value
    }));
  };
  
  // Toggle dark mode
  const toggleDarkMode = () => {
    setTheme(prev => ({
      ...prev,
      darkMode: !prev.darkMode
    }));
  };
  
  // Reset theme to defaults
  const resetTheme = () => {
    setTheme({
      primaryColor: '#3498db',
      secondaryColor: '#2ecc71',
      accentColor: '#f1c40f',
      dangerColor: '#e74c3c',
      backgroundColor: '#f5f5f5',
      cardColor: '#ffffff',
      darkMode: false,
    });
    
    setMessage({
      type: 'success',
      text: 'Theme reset to default colors!'
    });
    
    // Clear message after a delay
    setTimeout(() => {
      setMessage({ type: '', text: '' });
    }, 3000);
  };
  
  // Handle icon selection
  const handleIconSelect = (iconName) => {
    setProfile(prev => ({
      ...prev,
      icon: iconName
    }));
  };
  
  // Update profile
  const saveGeneralInfo = () => {
    // Simple validation - bio length
    if (profile.bio.length > 1000) {
      setMessage({
        type: 'error',
        text: 'Bio must be 1000 characters or less'
      });
      return;
    }
    
    // Save to localStorage
    const profileKey = `userProfile_${user.username}`;
    const profileData = {
      username: user.username, // Keep original username
      displayName: profile.displayName,
      icon: profile.icon,
      nameColor: profile.nameColor,
      bio: profile.bio
    };
    
    localStorage.setItem(profileKey, JSON.stringify(profileData));
    
    // Update the user context
    updateUser({
      ...user,
      displayName: profile.displayName,
      icon: profile.icon,
      nameColor: profile.nameColor
    });
    
    setMessage({
      type: 'success',
      text: 'Profile updated successfully!'
    });
    
    // Clear message after a delay
    setTimeout(() => {
      setMessage({ type: '', text: '' });
    }, 3000);
  };
  
  // Save theme settings
  const saveTheme = () => {
    // Save to localStorage
    const themeKey = `userTheme_${user.username}`;
    localStorage.setItem(themeKey, JSON.stringify(theme));
    
    // Apply theme to body for global styles (in a real app, you'd use a ThemeProvider)
    document.documentElement.style.setProperty('--primary-color', theme.primaryColor);
    document.documentElement.style.setProperty('--secondary-color', theme.secondaryColor);
    document.documentElement.style.setProperty('--accent-color', theme.accentColor);
    document.documentElement.style.setProperty('--danger-color', theme.dangerColor);
    document.documentElement.style.setProperty('--background-color', theme.backgroundColor);
    document.documentElement.style.setProperty('--card-color', theme.cardColor);
    
    // Toggle dark mode class if needed
    if (theme.darkMode) {
      document.body.classList.add('dark-mode');
    } else {
      document.body.classList.remove('dark-mode');
    }
    
    setMessage({
      type: 'success',
      text: 'Theme updated successfully!'
    });
    
    // Clear message after a delay
    setTimeout(() => {
      setMessage({ type: '', text: '' });
    }, 3000);
  };
  
  // Change password
  const changePassword = () => {
    // In a real app we'd verify the current password here
    if (!profile.currentPassword || !profile.newPassword || !profile.confirmPassword) {
      setMessage({
        type: 'error',
        text: 'Please fill in all password fields'
      });
      return;
    }
    
    if (profile.newPassword !== profile.confirmPassword) {
      setMessage({
        type: 'error',
        text: 'New passwords do not match'
      });
      return;
    }
    
    // For demo purposes, we'll just store the new password in localStorage
    // In a real app, you would send this to a secure API
    const passwordKey = `userPassword_${user.username}`;
    localStorage.setItem(passwordKey, profile.newPassword);
    
    setMessage({
      type: 'success',
      text: 'Password changed successfully!'
    });
    
    // Reset password fields
    setProfile(prev => ({
      ...prev,
      currentPassword: '',
      newPassword: '',
      confirmPassword: ''
    }));
    
    // Clear message after a delay
    setTimeout(() => {
      setMessage({ type: '', text: '' });
    }, 3000);
  };
  
  // Delete account
  const handleDeleteAccount = () => {
    if (!showDeleteConfirm) {
      setShowDeleteConfirm(true);
      return;
    }
    
    // Remove all user data
    const username = user.username;
    localStorage.removeItem(`journalEntries_${username}`);
    localStorage.removeItem(`welcomeSeen_${username}`);
    localStorage.removeItem(`userSince_${username}`);
    localStorage.removeItem(`userProfile_${username}`);
    localStorage.removeItem(`userTheme_${username}`);
    localStorage.removeItem(`userPassword_${username}`);
    localStorage.removeItem('moodJournalUser');
    
    // Log out and redirect to login page
    logout();
    navigate('/login');
  };
  
  // Cancel account deletion
  const cancelDelete = () => {
    setShowDeleteConfirm(false);
  };
  
  // Generate a color preview style
  const getColorPreviewStyle = (color) => ({
    display: 'inline-block',
    width: '24px',
    height: '24px',
    borderRadius: '50%',
    backgroundColor: color,
    marginRight: '10px',
    border: '2px solid #e0e0e0',
    verticalAlign: 'middle'
  });
  
  return (
    <div className="profile-editor-overlay">
      <div className="profile-editor-modal">
        <div className="profile-editor-header">
          <h2>Edit Your Profile</h2>
          <button className="close-button" onClick={onClose}>×</button>
        </div>
        
        <div className="profile-tabs">
          <button 
            className={`tab-button ${activeTab === 'general' ? 'active' : ''}`}
            onClick={() => setActiveTab('general')}
          >
            General
          </button>
          <button 
            className={`tab-button ${activeTab === 'theme' ? 'active' : ''}`}
            onClick={() => setActiveTab('theme')}
          >
            Theme
          </button>
          <button 
            className={`tab-button ${activeTab === 'password' ? 'active' : ''}`}
            onClick={() => setActiveTab('password')}
          >
            Password
          </button>
          <button 
            className={`tab-button ${activeTab === 'danger' ? 'active' : ''}`}
            onClick={() => setActiveTab('danger')}
          >
            Delete Account
          </button>
        </div>
        
        <div className="profile-editor-content">
          {message.text && (
            <div className={`profile-message ${message.type}`}>
              {message.text}
            </div>
          )}
          
          {activeTab === 'general' && (
            <div className="profile-section">
              <div className="profile-preview">
                <div className="profile-icon-container">
                  <IconSelector 
                    selectedIcon={profile.icon} 
                    onSelectIcon={handleIconSelect} 
                  />
                </div>
                <div className="name-and-color">
                  <span 
                    className="username-preview"
                    style={{ color: profile.nameColor }}
                  >
                    {profile.displayName}
                  </span>
                  <input
                    type="color"
                    value={profile.nameColor}
                    onChange={(e) => setProfile({...profile, nameColor: e.target.value})}
                    className="color-wheel-input"
                    title="Change name color"
                  />
                </div>
              </div>
              
              <div className="form-group">
                <label htmlFor="displayName">Display Name</label>
                <input
                  type="text"
                  id="displayName"
                  name="displayName"
                  value={profile.displayName}
                  onChange={handleChange}
                  className="animate-input"
                />
                <small className="form-help-text">This name will appear in your journal entries</small>
              </div>
              
              <div className="form-group">
                <label htmlFor="bio">Bio <span className="bio-counter">{profile.bio.length}/1000</span></label>
                <textarea
                  id="bio"
                  name="bio"
                  value={profile.bio}
                  onChange={handleChange}
                  placeholder="Tell us about yourself..."
                  className="animate-input bio-textarea"
                  maxLength={1000}
                />
              </div>
              
              <div className="profile-action-buttons">
                <button 
                  className="save-profile-button"
                  onClick={saveGeneralInfo}
                >
                  Save Changes
                </button>
                
                <button 
                  className="logout-button profile-logout-button"
                  onClick={() => {
                    logout();
                    navigate('/login');
                  }}
                >
                  Log Out
                </button>
              </div>
            </div>
          )}
          
          {activeTab === 'theme' && (
            <div className="profile-section">
              <div className="theme-preview" style={{
                backgroundColor: theme.backgroundColor,
                padding: '20px',
                borderRadius: '8px',
                marginBottom: '20px',
                border: '1px solid #e0e0e0'
              }}>
                <div style={{
                  backgroundColor: theme.cardColor,
                  borderRadius: '8px',
                  padding: '15px',
                  boxShadow: '0 2px 5px rgba(0, 0, 0, 0.1)',
                  borderTop: `3px solid ${theme.primaryColor}`
                }}>
                  <h3 style={{ color: theme.primaryColor }}>Theme Preview</h3>
                  <p>This is how your journal will look with these colors.</p>
                  
                  <div style={{ 
                    display: 'flex',
                    gap: '10px',
                    marginTop: '15px'
                  }}>
                    <button style={{
                      backgroundColor: theme.primaryColor,
                      color: 'white',
                      border: 'none',
                      borderRadius: '4px',
                      padding: '8px 16px'
                    }}>
                      Primary Button
                    </button>
                    
                    <button style={{
                      backgroundColor: theme.secondaryColor,
                      color: 'white',
                      border: 'none',
                      borderRadius: '4px',
                      padding: '8px 16px'
                    }}>
                      Secondary Button
                    </button>
                    
                    <button style={{
                      backgroundColor: theme.dangerColor,
                      color: 'white',
                      border: 'none',
                      borderRadius: '4px',
                      padding: '8px 16px'
                    }}>
                      Delete Button
                    </button>
                  </div>
                  
                  <div style={{
                    marginTop: '15px',
                    borderLeft: `4px solid ${theme.accentColor}`,
                    padding: '10px 15px',
                    backgroundColor: 'rgba(241, 196, 15, 0.1)',
                    borderRadius: '4px'
                  }}>
                    <p style={{ color: theme.darkMode ? '#f5f5f5' : '#333' }}>This is how your gratitude section will look.</p>
                  </div>
                </div>
              </div>
              
              <div className="theme-controls">
                <div className="form-row">
                  <div className="form-group">
                    <label>
                      <span style={getColorPreviewStyle(theme.primaryColor)}></span>
                      Primary Color
                    </label>
                    <input
                      type="color"
                      name="primaryColor"
                      value={theme.primaryColor}
                      onChange={handleThemeChange}
                      className="color-input"
                    />
                    <small className="form-help-text">Used for buttons, links, and accents</small>
                  </div>
                  
                  <div className="form-group">
                    <label>
                      <span style={getColorPreviewStyle(theme.secondaryColor)}></span>
                      Secondary Color
                    </label>
                    <input
                      type="color"
                      name="secondaryColor"
                      value={theme.secondaryColor}
                      onChange={handleThemeChange}
                      className="color-input"
                    />
                    <small className="form-help-text">Used for gradients and alternate buttons</small>
                  </div>
                </div>
                
                <div className="form-row">
                  <div className="form-group">
                    <label>
                      <span style={getColorPreviewStyle(theme.accentColor)}></span>
                      Accent Color
                    </label>
                    <input
                      type="color"
                      name="accentColor"
                      value={theme.accentColor}
                      onChange={handleThemeChange}
                      className="color-input"
                    />
                    <small className="form-help-text">Used for gratitude section and highlights</small>
                  </div>
                  
                  <div className="form-group">
                    <label>
                      <span style={getColorPreviewStyle(theme.dangerColor)}></span>
                      Danger Color
                    </label>
                    <input
                      type="color"
                      name="dangerColor"
                      value={theme.dangerColor}
                      onChange={handleThemeChange}
                      className="color-input"
                    />
                    <small className="form-help-text">Used for delete buttons and warnings</small>
                  </div>
                </div>
                
                <div className="form-row">
                  <div className="form-group">
                    <label>
                      <span style={getColorPreviewStyle(theme.backgroundColor)}></span>
                      Background Color
                    </label>
                    <input
                      type="color"
                      name="backgroundColor"
                      value={theme.backgroundColor}
                      onChange={handleThemeChange}
                      className="color-input"
                    />
                    <small className="form-help-text">Used for page background</small>
                  </div>
                  
                  <div className="form-group">
                    <label>
                      <span style={getColorPreviewStyle(theme.cardColor)}></span>
                      Card Color
                    </label>
                    <input
                      type="color"
                      name="cardColor"
                      value={theme.cardColor}
                      onChange={handleThemeChange}
                      className="color-input"
                    />
                    <small className="form-help-text">Used for card backgrounds</small>
                  </div>
                </div>
                
                <div className="dark-mode-toggle">
                  <label className="switch">
                    <input
                      type="checkbox"
                      checked={theme.darkMode}
                      onChange={toggleDarkMode}
                    />
                    <span className="slider round"></span>
                  </label>
                  <span className="dark-mode-label">Dark Mode</span>
                  <small className="form-help-text">(Coming Soon - Preview Only)</small>
                </div>
                
                <div className="theme-actions">
                  <button 
                    className="reset-theme-button"
                    onClick={resetTheme}
                  >
                    Reset to Default Theme
                  </button>
                  
                  <button 
                    className="save-theme-button"
                    onClick={saveTheme}
                  >
                    Apply Theme
                  </button>
                </div>
              </div>
            </div>
          )}
          
          {activeTab === 'password' && (
            <div className="profile-section">
              <div className="form-group">
                <label htmlFor="currentPassword">Current Password</label>
                <input
                  type="password"
                  id="currentPassword"
                  name="currentPassword"
                  value={profile.currentPassword}
                  onChange={handleChange}
                  className="animate-input"
                />
              </div>
              
              <div className="form-group">
                <label htmlFor="newPassword">New Password</label>
                <input
                  type="password"
                  id="newPassword"
                  name="newPassword"
                  value={profile.newPassword}
                  onChange={handleChange}
                  className="animate-input"
                />
              </div>
              
              <div className="form-group">
                <label htmlFor="confirmPassword">Confirm New Password</label>
                <input
                  type="password"
                  id="confirmPassword"
                  name="confirmPassword"
                  value={profile.confirmPassword}
                  onChange={handleChange}
                  className="animate-input"
                />
              </div>
              
              <button 
                className="save-password-button"
                onClick={changePassword}
              >
                Change Password
              </button>
            </div>
          )}
          
          {activeTab === 'danger' && (
            <div className="profile-section danger-section">
              <h3>Delete Account</h3>
              <p className="danger-text">
                Warning: This action is permanent and irreversible. All your journal entries and profile data will be deleted.
              </p>
              
              {!showDeleteConfirm ? (
                <button 
                  className="delete-account-button"
                  onClick={handleDeleteAccount}
                >
                  Delete My Account
                </button>
              ) : (
                <div className="confirm-delete-container">
                  <p className="confirm-text">Are you sure? This cannot be undone.</p>
                  <div className="confirm-buttons">
                    <button 
                      className="confirm-delete-button"
                      onClick={handleDeleteAccount}
                    >
                      Yes, Delete Everything
                    </button>
                    <button 
                      className="cancel-delete-button"
                      onClick={cancelDelete}
                    >
                      Cancel
                    </button>
                  </div>
                </div>
              )}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default ProfileEditor;
